import { 
	Grid,
	Card,
	Typography,
	CardContent,
	TextField,
	Button
} from '@mui/material';
import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';

export default function UserForm() {

	const navigate = useNavigate();
	const params = useParams();
	const [loading, setLoading] = useState(false);
  	const [editing, setEditing] = useState(false); 
	const [user, setUser] = useState({
		Nombre:'',
		Email:'',
		Password:''
	});

	const cargarUser = async (id) => {
    const res = await fetch("http://localhost:4000/users/" + id);
    const data = await res.json();
    setUser({ nombre: data.name, email: data.email, password: data.password });
    setEditing(true);
  };

	const handleSubmit = async(e) =>{
		e.preventDefault();
		const result = await fetch('https://localhost:4000/users', {
			method:'POST',
			body: JSON.stringify(user),
			headers:{"Content-Type": "application/json"}
		})
		await result.json()
		navigate('/')

	}

	const handleChange = (e) =>{
		setUser({...user, [e.target.name] : e.target.value})
	}

	return (
		<Grid container 
			  direction="column"
			  alignItems="center"
			  justifyContent="center"
			  >
			<Grid item xs={3}>
				<Card 
				 sx={{mt:5}}
				 style={{
				 	backgroundColor:'#1e272e',
				 	padding:'1rem'
				 }}
				>
					<Typography 
						variant='filled'
						textAlign='center'
						color='white'
						>
						Create User
					</Typography>
					<CardContent>
						<form onSubmit={handleSubmit}>
							<TextField 
								variant='filled'
								label='Nombre'
								sx={{
									display:'block',
									margin:'.5rem 0'
								}}
								name='nombre'
								onChange={handleChange}
								inputProps={{style:{color:'white'}}}
								InputLabelProps={{style:{color:'white'}}}
							/>
							<TextField 
								variant='filled'
								label='Email'
								sx={{
									display:'block',
									margin:'.5rem 0'
								}}
								name='email'
								onChange={handleChange}
								inputProps={{style:{color:'white'}}}
								InputLabelProps={{style:{color:'white'}}}
							/>
							<TextField 
								variant='filled'
								label='Password'
								sx={{
									display:'block',
									margin:'.5rem 0'
								}}
								name='password'
								onChange={handleChange}
								inputProps={{style:{color:'white'}}}
								InputLabelProps={{style:{color:'white'}}} 	
							/>
							<Button 
								variant='contained'
								color='primary'
								type='submit'
								>
								Guardar
							</Button>
						</form>
					</CardContent>
				</Card>
			</Grid>
		</Grid>
	)
}
